<div class="wpal-ecomm-field" data-field="{{data.password1_name}}">
    <label for="{{data.password1_name}}">
        {{{data.password1_label}}}
    </label>
    <span class="wpal-ecomm-password-input">
        <input type="password" name="{{data.password1_name}}" id="{{data.password1_name}}" autocomplete="off">
        <span class="show-hide-password-icon show-password-input"></span>
    </span>
</div>

